import React from 'react';
import Unity, { UnityContent } from 'react-unity-webgl';
import { Button } from '@material-ui/core';

const unityContent = new UnityContent(
  'Build/unityBall.json',
  'Build/UnityLoader.js'
);

// ヘッダーを作成
const Header: React.FC = () => (
  <div>
    <p>matchima @2021/03</p>
  </div>
);

// ボタンを作成
const MoveRightButton: React.FC = () => (
  <Button
    type="button"
    onClick={(): void => {
      // unityContent.send("オブジェクト名", "メソッド名（Unity側でつくられている）", 1);
      unityContent.send("Ball", "MoveRight", 1);
    }
    }
  >
    右へ
  </Button>
);
// ボタンを作成
const MoveLeftButton: React.FC = () => (
  <Button variant="contained" color="primary"
    type="button"
    onClick={(): void => {
      // unityContent.send("オブジェクト名", "メソッド名（Unity側でつくられている）", 1);
      unityContent.send("Ball", "MoveLeft", 1);
    }
    }
  >
    左へ
  </Button>
);

const App: React.FC = () => (
    <>
    <Header />
    <MoveLeftButton />
    <MoveRightButton />


    <Button variant="contained">Default</Button>
    <Button variant="contained" color="primary">
      Primary
    </Button>
    <Button variant="contained" color="secondary">
      Secondary
    </Button>
    <Button variant="contained" disabled>
      Disabled
    </Button>
    <Button variant="contained" color="primary" href="#contained-buttons">
      Link
    </Button>


    <Unity unityContent={unityContent} />
    </>
);

export default App;
