import React from 'react';
import Unity, { UnityContent } from 'react-unity-webgl';
import { Button } from '@material-ui/core';

const unityContent = new UnityContent(
  'Build/CarColorChange.json',
  'Build/UnityLoader.js'
);

// ヘッダーを作成
// const Header: React.FC = () => (
//   <div>
//     <p>matchima @2021/03</p>
//   </div>
// );

// 左へボタンを作成
const MoveLeftButton: React.FC = () => (
  <Button size="large" variant="contained" color="primary"
    type="button"
    onClick={(): void => {
      // unityContent.send("オブジェクト名", "メソッド名（Unity側でつくられている）", 1);
      unityContent.send("Ball", "MoveLeft", -1);
    }
    }
  >
    左へ
  </Button>
);

// 右へボタンを作成
const MoveRightButton: React.FC = () => (
  <Button size="large" variant="contained" color="secondary"
    type="button"
    onClick={(): void => {
      // unityContent.send("オブジェクト名", "メソッド名（Unity側でつくられている）", 1);
      unityContent.send("Ball", "MoveRight", 1);
      unityContent.send("Audi R8", "MoveRight", 1);
    }
    }
  >
    右へ
  </Button>
);

// 色変更ボタンを作成
const ChangeColorBtn: React.FC = () => (
  <Button size="large" variant="contained" color="primary"
    type="button"
    onClick={(): void => {
      // unityContent.send("オブジェクト名", "メソッド名（Unity側でつくられている）", 1);
      unityContent.send("Audi R8", "ChangeColor", 1);
    }
    }
  >
    青へ変更
  </Button>
);

const App: React.FC = () => (
    <>
    {/* <Header /> */}
      <ChangeColorBtn />
      <MoveLeftButton />
      <MoveRightButton />
    <Unity unityContent={unityContent} />
    </>
);

export default App;
